INSERT INTO objetivo_eurace (id, codigo, nombre, descripcion) VALUES
(1, ' 5.2.1.', 'La aplicación de los conocimientos fundamentales', ' El uso de los conocimientos básicos y avanzados en las áreas de matemática, ciencias naturales, ciencias humanitarias, ciencias socioeconómicas y conocimientos técnicos en un contexto interdisciplinario para resolver problemas complejos de ingeniería el campo de estudio pertinente.'),
(2, ' 5.2.2.', 'Ingeniería análisis', 'Definición y resolución de problemas de análisis de ingeniería compleja en el campo de estudio pertinente con el uso de conocimientos básicos y avanzados de métodos analíticos modernos y modelos.'),
(3, ' 5.2.3.', 'Diseño de ingeniería', ' Realización de proyectos de ingeniería complejos en diseño de objetos sistemas y procesos técnicos en el campo de estudio pertinente teniendo en cuenta los aspectos económicos ambientales sociales y de otro tipo.'),
(4, ' 5.2.4.', 'Investigación', '  La investigación en la solución de problemas complejos de ingeniería en el campo de estudio pertinente  incluyendo la formulación experimental análisis e interpretación de datos utilizando conocimientos básicos y avanzados. '),
(5, ' 5.2.5.', 'Ingeniería práctica', '  Creación selección y aplicación de los recursos y métodos necesarios incluyendo la predicción y la modelación técnicas modernas y herramientas de TI para resolver problemas complejos de ingeniería en el campo de estudio pertinente teniendo en cuenta las posibles restricciones.   '),
(6, ' 5.2.6.', 'La especialización y enfoque en el mercado de trabajo', ' Demostración de competencias relacionadas con los problemas objetivos y tipos de actividades complejas de ingeniería específicos correspondientes a la formación y el perfil de la dirección en las empresas y organizaciones -. Potenciales empleadores.'),
(7, ' 5.3.1.', 'Gestión', ' El uso de los conocimientos básicos y avanzados en el campo de la gestión para gestionar las actividades de ingeniería complejos en el campo de estudio pertinente.'),
(8, ' 5.3.2.', 'Comunicación', ' La comunicación eficaz incluso en un idioma extranjero en un entorno profesional el desarrollo de la documentación presentación y protección de los resultados de algunas de las actividades complejas de ingeniería en el campo de estudio pertinente.'),
(9, ' 5.3.3.', 'Trabajo individual y en equipo', 'Un trabajo efectivo individual y el trabajo como miembro o líder del equipo incluyendo multidisciplinar con la división de la responsabilidad y la autoridad para resolver problemas complejos de ingeniería en el campo de estudio pertinente.'),
(10, ' 5.3.4.', 'La ética profesional', 'La responsabilidad personal y compromiso con la ética profesional en las actividades complejas de ingeniería. '),
(11, ' 5.3.5.', 'La responsabilidad social', 'Actividades de ingeniería para resolver problemas complejos de ingeniería en el campo de estudio pertinente teniendo en cuenta los aspectos jurídicos y culturales de la protección de la salud y la seguridad la responsabilidad social de sus decisiones para garantizar el desarrollo sostenible.'),
(12, ' 5.3.6.', 'El aprendizaje a lo largo de la vida', 'La conciencia de la necesidad y la capacidad para el aprendizaje autónomo y el desarrollo profesional continuo.');
